var p = require('fd/platform')

;[
// File
  [ '#N', 'new'],
  [ '#O', 'open'],
  [ '#W', 'close'],
  [ '#S', 'save'],
  ['!#S', 'saveAs'],
  ['!#E', 'export'],
  [ '#P', 'print'],
  ['!#P', 'pageSetup'],

// Edit
  [ '#Z', 'undo'],
  [p({win: '#Y', osx: '!#Z'}), 'redo'],
  [ '#X', 'cut'],
  [ '#C', 'copy'],
  [ '#V', 'paste'],
  ['!#V', 'pasteInPlace'],
  [ '#D', 'duplicate'],
  [  'Delete', 'delete'],
  [ '#A', 'selectAll'],
  [':#C', 'copyStyle'],
  [':#V', 'pasteStyle'],
  // pickColor

// Insert
  [  'C', 'insertCurve'],
  [  'P', 'insertPencil'],
  [  'L', 'insertLine'],
  [  'R', 'insertRectangle'],
  [  'U', 'insertRounded'],
  [  'E', 'insertEllipse'],
  [  'T', 'insertText'],
  [  'S', 'slice'],

// View
  [ '#0', 'zoom0'],
  [ '#1', 'zoomCenter'],
  [ '#=', 'zoomIn'],
  [ '#+', 'zoomIn'],
  [ '#-', 'zoomOut'],
  [ '#_', 'zoomOut'],

// Arrange
  [ '#G', 'group'],
  ['!#G', 'ungroup'],
  [ '#F', 'filterTree'],

// Miscellaneous
  [ '\x1B', 'selectParent'],
].forEach(function(b) {
  exports[b[0]] = b[1]
})
