'use strict'

require('fd/events')
require('fd/commands')
require('fd/extensions')
require('fd/theme/default/main.css')

var Native = require('native')
var Editor = require('fd/ui/editor')

var editor = new Editor
global.Freedraw = module.exports = editor

Native.hooks.getData = editor.getData.bind(editor)
Native.hooks.loadData = editor.loadData.bind(editor)
editor.start()

Native.done()
