var Vec2 = require('fd/vec2')

exports.position = Vec2.zero
var buttons = exports.buttons = {
  left: false,
  middle: false,
  right: false,
  fourth: false,
  fifth: false,
}

function update(e) {
  exports.position = new Vec2(e.clientX, e.clientY)
  buttons.left = !!(e.buttons & 0x1)
  buttons.right = !!(e.buttons & 0x2)
  buttons.middle = !!(e.buttons & 0x4)
  buttons.fourth = !!(e.buttons & 0x8)
  buttons.fifth = !!(e.buttons & 0x10)
}

document.addEventListener('mousemove', update)
document.addEventListener('mousedown', update)
document.addEventListener('mouseup', update)
