'use strict'

var name =
  /OS X/.test(navigator.userAgent) ? 'osx' :
  /Windows/.test(navigator.userAgent) ? 'win' : 'others'

module.exports = function(o) {
  return o[name] || o.others
}

module.exports.type = name
module.exports.isOSX = name === 'osx'
module.exports.isWindows = name === 'win'
module.exports.isWebKit = /WebKit/.test(navigator.userAgent)
