var h = require('fd/html')
var Matrix = require('fd/matrix')
var commands = require('fd/commands')
var mouse = require('fd/mouse')

function MarkingMenu(editor) {
  this.editor = editor

  this.el = h('.marking-menu')

  commands.add('showMenus', this.show, this)
}

MarkingMenu.prototype.show = function() {
  this.el.style.transform = Matrix.translateBy(mouse.position).toCSS()
  this.el.style.display = 'block'
}

module.exports = MarkingMenu
